$(document).ready(function () {
var isMessagingEnabledAndUserNotLoggedIn = 'false';
var isChatMessage = false;
var isMessageToSelf = false;
var contactAdvertiserBtn;

if (isMessagingEnabledAndUserNotLoggedIn === 'true') {
contactAdvertiserBtn = $('#contactAdvertiser');
showMessagingPopover();

$(window).scroll(function () {
showMessagingPopover();
});
}

function showMessagingPopover() {
if (isScrolledIntoView(contactAdvertiserBtn)) {
if ($('#messaging-popover-info').length === 0) {
contactAdvertiserBtn.popover('show');
}
} else {
console.log("Not inView");
}
}

function isScrolledIntoView(elem) {
var docViewTop = $(window).scrollTop();
var docViewBottom = docViewTop + $(window).height();

var elemTop = $(elem).offset().top;
var elemBottom = elemTop + $(elem).height();

return ((elemBottom <= docViewBottom) && (elemTop >= docViewTop));
}

